java.util.concurrent.BlockingQueue<java.lang.String> urlForBuild;
  @org.springframework.beans.factory.annotation.Autowired
  com.containersolutions.mesos.scheduler.TaskInfoGradleBuildDocker taskInfoGradleBuildDocker;

  public void add(java.lang.String repoUrl) {
    urlForBuild.add(repoUrl);
  }

  public java.util.Optional<java.lang.String> getFirst() {
    if (urlForBuild.isEmpty()) return java.util.Optional.empty();
    return java.util.Optional.of(urlForBuild.poll());
  }

  public BuildService() {
    this.urlForBuild = new java.util.concurrent.ArrayBlockingQueue<>(10);
  }

