class FbiCommand extends com.netflix.hystrix.HystrixCommand<info.developerblog.services.user.TFbiResponse> {
  private final info.developerblog.services.user.TFbiService.Client client
  private final int fee
  private final int hippoCount

  protected FbiCommand(com.netflix.hystrix.HystrixCommandGroupKey group,
                       info.developerblog.services.user.TFbiService.Client client,
                       int hippoCount, int fee) {
    super(group)
    this.hippoCount = hippoCount
    this.fee = fee
    this.client = client
  }

  @java.lang.Override
  protected info.developerblog.services.user.TFbiResponse run() throws java.lang.Exception {
    return client.fink(new info.developerblog.services.auth.TAuthToken("ABCD"), hippoCount, fee)
  }

  @java.lang.Override
  protected info.developerblog.services.user.TFbiResponse getFallback() {
    println "fallback"
    return new info.developerblog.services.user.TFbiResponse()
  }
}

@org.springframework.context.annotation.Bean
ru.aatarasoff.thrift.api.gateway.core.AuthTokenExchanger<info.developerblog.services.auth.TAuthToken, info.developerblog.services.auth.TUser> authTokenExchanger() {
    return new ru.aatarasoff.thrift.api.gateway.core.AuthTokenExchanger<info.developerblog.services.auth.TAuthToken, info.developerblog.services.auth.TUser>() {
        @java.lang.Override
        info.developerblog.services.auth.TAuthToken createEmptyAuthToken() {
            return new info.developerblog.services.auth.TAuthToken()
        }

        @java.lang.Override
        info.developerblog.services.auth.TUser process(info.developerblog.services.auth.TAuthToken authToken) throws org.apache.thrift.TException {
            if ("ABCD".equals(authToken.getToken())) {
                return new info.developerblog.services.auth.TUser().setUserId(1L)
            }

            throw new info.developerblog.services.auth.TUnauthorizedException("!!!")
        }
    }
}

@ru.trylogic.spring.boot.thrift.annotation.ThriftHandler("/api")
public static class ThriftController implements info.developerblog.services.user.TFbiService.Iface {

  @java.lang.Override
  info.developerblog.services.user.TFbiResponse fink(info.developerblog.services.auth.TUser user, int hippoCount, int fee) throws org.apache.thrift.TException {
    log.info 'fink: {}, {}, {}', user, hippoCount, fee
    return new info.developerblog.services.user.TFbiResponse().setLastFink('ISO8601 (' +  ZonedDateTime.now() + ')')
  }
}

