@org.springframework.web.bind.annotation.RestController
  public static class HippoController {
    private java.util.concurrent.atomic.AtomicInteger hippoCount = new java.util.concurrent.atomic.AtomicInteger(Integer.MAX_VALUE);

    @org.springframework.web.bind.annotation.RequestMapping(value = '/rent', method = RequestMethod.GET)
    def rent(@org.springframework.web.bind.annotation.RequestParam(defaultValue = '1') java.lang.Integer hippoRequest) {
      return [hippoRemain: hippoCount.getAndAdd(-1 * hippoRequest)]
    }
  }

